import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  // State untuk data brand dan target
  const [brand, setBrand] = useState('Carrot Cake w/ Cheese Frosting');
  const [targetCost, setTargetCost] = useState(155);
  const [targetPieces, setTargetPieces] = useState(19);

  // State untuk bahan baku sesuai template spreadsheet
  const [ingredients, setIngredients] = useState([
    { id: 1, name: 'Carrot', usage: 360, unit: 'gr', purchasePrice: 25000, purchaseUnit: 1000, purchaseUnitType: 'gram', targetCost: 9000 },
    { id: 2, name: 'Flour All Purpose', usage: 240, unit: 'gr', purchasePrice: 17000, purchaseUnit: 1000, purchaseUnitType: 'gram', targetCost: 4080 },
    { id: 3, name: 'Baking Powder (2 tsp)', usage: 20, unit: 'gr', purchasePrice: 12500, purchaseUnit: 136, purchaseUnitType: 'gram', targetCost: 1838 },
    { id: 4, name: 'Baking Soda (2 tsp)', usage: 20, unit: 'gr', purchasePrice: 9000, purchaseUnit: 50, purchaseUnitType: 'gram', targetCost: 3600 },
    { id: 5, name: 'Cinnamon Powder (2 tsp)', usage: 10, unit: 'gr', purchasePrice: 13500, purchaseUnit: 35, purchaseUnitType: 'gram', targetCost: 3857 },
    { id: 6, name: 'Salt (1 tsp)', usage: 3, unit: 'gr', purchasePrice: 1000, purchaseUnit: 1000, purchaseUnitType: 'gram', targetCost: 0 },
    { id: 7, name: 'Walnuts', usage: 100, unit: 'gr', purchasePrice: 45000, purchaseUnit: 150, purchaseUnitType: 'gram', targetCost: 30000 },
    { id: 8, name: 'Sugar', usage: 300, unit: 'gr', purchasePrice: 23000, purchaseUnit: 1000, purchaseUnitType: 'gram', targetCost: 6900 },
    { id: 9, name: 'Canola Oil', usage: 225, unit: 'ml', purchasePrice: 129000, purchaseUnit: 2000, purchaseUnitType: 'ml', targetCost: 14513 },
    { id: 10, name: 'Egg (4 pcs)', usage: 280, unit: 'gr', purchasePrice: 30000, purchaseUnit: 1000, purchaseUnitType: 'gram', targetCost: 8400 },
    { id: 11, name: 'Cheese Frosting', usage: 110, unit: 'gr', purchasePrice: 33000, purchaseUnit: 320, purchaseUnitType: 'gram', targetCost: 11344 },
  ]);

  // State untuk consumable
  const [consumable, setConsumable] = useState({
    cost: 0,
    quantity: 100,
    unit: 'unit'
  });

  // State untuk perhitungan Go Food
  const [goFoodPercentage, setGoFoodPercentage] = useState(20);
  const [taxPercentage, setTaxPercentage] = useState(10);
  const [profitMargin, setProfitMargin] = useState(40);

  // Fungsi untuk menghitung biaya per bahan
  const calculateIngredientCost = (ingredient) => {
    if (ingredient.purchaseUnit > 0 && ingredient.purchasePrice > 0) {
      return (ingredient.usage / ingredient.purchaseUnit) * ingredient.purchasePrice;
    }
    return 0;
  };

  // Fungsi untuk menghitung total biaya bahan baku
  const calculateTotalMaterialCost = () => {
    return ingredients.reduce((total, ingredient) => {
      return total + calculateIngredientCost(ingredient);
    }, 0);
  };

  // Fungsi untuk menghitung total biaya produksi
  const calculateTotalProductionCost = () => {
    return calculateTotalMaterialCost() + (consumable.cost || 0);
  };

  // Fungsi untuk menghitung HPP per piece
  const calculateHPPPerPiece = () => {
    return calculateTotalProductionCost() / targetPieces;
  };

  // Fungsi untuk menghitung harga jual dine in
  const calculateDineInPrice = () => {
    const hpp = calculateHPPPerPiece();
    return hpp / (1 - (profitMargin / 100));
  };

  // Fungsi untuk menghitung laba kotor
  const calculateGrossProfit = () => {
    const dineInPrice = calculateDineInPrice();
    const hpp = calculateHPPPerPiece();
    return dineInPrice - hpp;
  };

  // Fungsi untuk menghitung biaya Go Food
  const calculateGoFoodCost = () => {
    const dineInPrice = calculateDineInPrice();
    return dineInPrice * (goFoodPercentage / 100);
  };

  // Fungsi untuk menghitung pajak restoran
  const calculateRestaurantTax = () => {
    const dineInPrice = calculateDineInPrice();
    return dineInPrice * (taxPercentage / 100);
  };

  // Fungsi untuk menghitung harga jual Go Food
  const calculateGoFoodPrice = () => {
    const dineInPrice = calculateDineInPrice();
    const goFoodCost = calculateGoFoodCost();
    const tax = calculateRestaurantTax();
    return dineInPrice + goFoodCost + tax;
  };

  // Fungsi untuk mengupdate data bahan
  const updateIngredient = (id, field, value) => {
    setIngredients(ingredients.map(ingredient =>
      ingredient.id === id ? { ...ingredient, [field]: value } : ingredient
    ));
  };

  // Fungsi untuk menambah bahan baru
  const addIngredient = () => {
    const newId = ingredients.length > 0 ? Math.max(...ingredients.map(i => i.id)) + 1 : 1;
    setIngredients([
      ...ingredients,
      {
        id: newId,
        name: `Bahan ${newId}`,
        usage: 0,
        unit: 'gr',
        purchasePrice: 0,
        purchaseUnit: 1,
        purchaseUnitType: 'gram',
        targetCost: 0
      }
    ]);
  };

  // Fungsi untuk menghapus bahan
  const removeIngredient = (id) => {
    if (ingredients.length > 1) {
      setIngredients(ingredients.filter(ingredient => ingredient.id !== id));
    }
  };

  return (
    <div className="container-fluid mt-3">
      <div className="row">
        <div className="col-12">
          <h1 className="text-center mb-3">RESEP PRODUK</h1>
          <div className="row mb-4">
            <div className="col-md-6">
              <div className="input-group mb-3">
                <span className="input-group-text bg-primary text-white fw-bold">BRAND</span>
                <input
                  type="text"
                  className="form-control"
                  value={brand}
                  onChange={(e) => setBrand(e.target.value)}
                  placeholder="Masukkan nama brand"
                />
              </div>
            </div>
            <div className="col-md-3">
              <div className="input-group mb-3">
                <span className="input-group-text bg-success text-white fw-bold">Target Cost</span>
                <input
                  type="number"
                  className="form-control"
                  value={targetCost}
                  onChange={(e) => setTargetCost(parseFloat(e.target.value) || 0)}
                />
              </div>
            </div>
            <div className="col-md-3">
              <div className="input-group mb-3">
                <span className="input-group-text bg-info text-white fw-bold">Target Pieces</span>
                <input
                  type="number"
                  className="form-control"
                  value={targetPieces}
                  onChange={(e) => setTargetPieces(parseFloat(e.target.value) || 1)}
                  min="1"
                />
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Tabel Resep */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="table-responsive">
            <table className="table table-bordered table-striped">
              <thead className="table-dark">
                <tr>
                  <th width="20%">MENU</th>
                  <th width="10%" className="text-center">RESEP</th>
                  <th width="5%" className="text-center">SATUAN PAKAI</th>
                  <th width="15%" className="text-center">HARGA BELI</th>
                  <th width="10%" className="text-center">SATUAN BELI</th>
                  <th width="10%" className="text-center">SATUAN</th>
                  <th width="15%" className="text-center">TARGET COST</th>
                  <th width="15%" className="text-center">BIAYA AKTUAL</th>
                </tr>
              </thead>
              <tbody>
                {ingredients.map((ingredient) => (
                  <tr key={ingredient.id}>
                    <td>
                      <input
                        type="text"
                        className="form-control form-control-sm"
                        value={ingredient.name}
                        onChange={(e) => updateIngredient(ingredient.id, 'name', e.target.value)}
                      />
                    </td>
                    <td>
                      <input
                        type="number"
                        className="form-control form-control-sm text-center"
                        value={ingredient.usage}
                        onChange={(e) => updateIngredient(ingredient.id, 'usage', parseFloat(e.target.value) || 0)}
                      />
                    </td>
                    <td>
                      <select
                        className="form-select form-select-sm"
                        value={ingredient.unit}
                        onChange={(e) => updateIngredient(ingredient.id, 'unit', e.target.value)}
                      >
                        <option value="gr">gr</option>
                        <option value="ml">ml</option>
                        <option value="kg">kg</option>
                        <option value="liter">liter</option>
                        <option value="pcs">pcs</option>
                      </select>
                    </td>
                    <td>
                      <div className="input-group input-group-sm">
                        <span className="input-group-text">Rp</span>
                        <input
                          type="number"
                          className="form-control text-end"
                          value={ingredient.purchasePrice}
                          onChange={(e) => updateIngredient(ingredient.id, 'purchasePrice', parseFloat(e.target.value) || 0)}
                        />
                      </div>
                    </td>
                    <td>
                      <input
                        type="number"
                        className="form-control form-control-sm text-center"
                        value={ingredient.purchaseUnit}
                        onChange={(e) => updateIngredient(ingredient.id, 'purchaseUnit', parseFloat(e.target.value) || 1)}
                      />
                    </td>
                    <td>
                      <select
                        className="form-select form-select-sm"
                        value={ingredient.purchaseUnitType}
                        onChange={(e) => updateIngredient(ingredient.id, 'purchaseUnitType', e.target.value)}
                      >
                        <option value="gram">gram</option>
                        <option value="ml">ml</option>
                        <option value="kg">kg</option>
                        <option value="liter">liter</option>
                        <option value="pcs">pcs</option>
                      </select>
                    </td>
                    <td className="text-end fw-bold">
                      Rp {ingredient.targetCost.toLocaleString('id-ID')}
                    </td>
                    <td className="text-end fw-bold text-success">
                      Rp {calculateIngredientCost(ingredient).toLocaleString('id-ID')}
                    </td>
                  </tr>
                ))}
                <tr>
                  <td colSpan="6" className="text-end fw-bold">Total</td>
                  <td className="text-end fw-bold">Rp {ingredients.reduce((sum, i) => sum + i.targetCost, 0).toLocaleString('id-ID')}</td>
                  <td className="text-end fw-bold text-primary">
                    Rp {calculateTotalMaterialCost().toLocaleString('id-ID')}
                  </td>
                </tr>
              </tbody>
            </table>
          </div>

          <div className="d-flex justify-content-between mb-3">
            <button className="btn btn-success" onClick={addIngredient}>
              + Tambah Bahan
            </button>
            <button className="btn btn-danger" onClick={() => {
              setIngredients(ingredients.slice(0, -1));
            }} disabled={ingredients.length <= 1}>
              - Hapus Bahan Terakhir
            </button>
          </div>
        </div>
      </div>

      {/* Biaya Consumable */}
      <div className="row mb-4">
        <div className="col-md-6">
          <div className="card">
            <div className="card-header bg-warning">
              <h5 className="mb-0">Consumable</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Biaya Consumable (Rp)</label>
                  <div className="input-group">
                    <span className="input-group-text">Rp</span>
                    <input
                      type="number"
                      className="form-control"
                      value={consumable.cost}
                      onChange={(e) => setConsumable({...consumable, cost: parseFloat(e.target.value) || 0})}
                    />
                  </div>
                  <small className="text-muted">Contoh: Packaging, sedotan, dll</small>
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">Jumlah Unit</label>
                  <input
                    type="number"
                    className="form-control"
                    value={consumable.quantity}
                    onChange={(e) => setConsumable({...consumable, quantity: parseFloat(e.target.value) || 0})}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Ringkasan Perhitungan */}
        <div className="col-md-6">
          <div className="card">
            <div className="card-header bg-info text-white">
              <h5 className="mb-0">Ringkasan Perhitungan</h5>
            </div>
            <div className="card-body">
              <div className="row mb-2">
                <div className="col-8">Total Biaya Bahan Baku:</div>
                <div className="col-4 text-end fw-bold">
                  Rp {calculateTotalMaterialCost().toLocaleString('id-ID')}
                </div>
              </div>
              <div className="row mb-2">
                <div className="col-8">Biaya Consumable:</div>
                <div className="col-4 text-end fw-bold">
                  Rp {consumable.cost.toLocaleString('id-ID')}
                </div>
              </div>
              <div className="row mb-2">
                <div className="col-8">Total Biaya Produksi:</div>
                <div className="col-4 text-end fw-bold text-primary">
                  Rp {calculateTotalProductionCost().toLocaleString('id-ID')}
                </div>
              </div>
              <div className="row mb-2">
                <div className="col-8">Jumlah Pieces:</div>
                <div className="col-4 text-end fw-bold">
                  {targetPieces} pcs
                </div>
              </div>
              <hr />
              <div className="row mb-2">
                <div className="col-8">HPP/COGS per Piece:</div>
                <div className="col-4 text-end fw-bold text-success">
                  Rp {calculateHPPPerPiece().toLocaleString('id-ID')}
                </div>
              </div>
              <div className="row mb-2">
                <div className="col-8">Target Cost per Piece:</div>
                <div className="col-4 text-end fw-bold">
                  Rp {targetCost.toLocaleString('id-ID')}
                </div>
              </div>
              <div className="row mb-2">
                <div className="col-8">Selisih:</div>
                <div className="col-4 text-end fw-bold" style={{
                  color: targetCost - calculateHPPPerPiece() >= 0 ? 'green' : 'red'
                }}>
                  Rp {(targetCost - calculateHPPPerPiece()).toLocaleString('id-ID')}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Perhitungan Go Food */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="card">
            <div className="card-header bg-danger text-white">
              <h4 className="mb-0">PERHITUNGAN GO FOOD</h4>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-8">
                  <div className="table-responsive">
                    <table className="table table-bordered">
                      <thead>
                        <tr>
                          <th width="40%">ITEM</th>
                          <th width="20%" className="text-center">NILAI</th>
                          <th width="20%" className="text-center">PERSENTASE</th>
                          <th width="20%" className="text-center">RUMUS</th>
                        </tr>
                      </thead>
                      <tbody>
                        <tr>
                          <td>HPP/COGS</td>
                          <td className="text-end fw-bold">
                            Rp {calculateHPPPerPiece().toLocaleString('id-ID')}
                          </td>
                          <td className="text-center">
                            <div className="input-group input-group-sm">
                              <input
                                type="number"
                                className="form-control text-center"
                                value={profitMargin}
                                onChange={(e) => setProfitMargin(parseFloat(e.target.value) || 0)}
                              />
                              <span className="input-group-text">%</span>
                            </div>
                          </td>
                          <td className="text-muted">HPP = Total Biaya / Jumlah Pieces</td>
                        </tr>
                        <tr>
                          <td>Harga Jual Dine In</td>
                          <td className="text-end fw-bold text-success">
                            Rp {calculateDineInPrice().toLocaleString('id-ID')}
                          </td>
                          <td className="text-center">
                            {((1 - (profitMargin / 100)) * 100).toFixed(0)}%
                          </td>
                          <td className="text-muted">Harga = HPP / (1 - Margin)</td>
                        </tr>
                        <tr>
                          <td>Laba Kotor</td>
                          <td className="text-end fw-bold" style={{
                            color: calculateGrossProfit() >= 0 ? 'green' : 'red'
                          }}>
                            Rp {calculateGrossProfit().toLocaleString('id-ID')}
                          </td>
                          <td className="text-center">
                            {((calculateGrossProfit() / calculateDineInPrice()) * 100).toFixed(0)}%
                          </td>
                          <td className="text-muted">Laba = Harga - HPP</td>
                        </tr>
                        <tr>
                          <td>Biaya Go Food</td>
                          <td className="text-end fw-bold">
                            Rp {calculateGoFoodCost().toLocaleString('id-ID')}
                          </td>
                          <td className="text-center">
                            <div className="input-group input-group-sm">
                              <input
                                type="number"
                                className="form-control text-center"
                                value={goFoodPercentage}
                                onChange={(e) => setGoFoodPercentage(parseFloat(e.target.value) || 0)}
                              />
                              <span className="input-group-text">%</span>
                            </div>
                          </td>
                          <td className="text-muted">Biaya = Harga Dine In  %</td>
                        </tr>
                        <tr>
                          <td>Pajak Restoran</td>
                          <td className="text-end fw-bold">
                            Rp {calculateRestaurantTax().toLocaleString('id-ID')}
                          </td>
                          <td className="text-center">
                            <div className="input-group input-group-sm">
                              <input
                                type="number"
                                className="form-control text-center"
                                value={taxPercentage}
                                onChange={(e) => setTaxPercentage(parseFloat(e.target.value) || 0)}
                              />
                              <span className="input-group-text">%</span>
                            </div>
                          </td>
                          <td className="text-muted">Pajak = Harga Dine In  %</td>
                        </tr>
                        <tr className="table-success">
                          <td><strong>Harga Jual Go Food</strong></td>
                          <td className="text-end fw-bold text-danger fs-5">
                            Rp {calculateGoFoodPrice().toLocaleString('id-ID')}
                          </td>
                          <td colSpan="2" className="text-muted text-center">
                            Total = Harga + Biaya GoFood + Pajak
                          </td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                </div>
                <div className="col-md-4">
                  <div className="card">
                    <div className="card-header bg-secondary text-white">
                      <h5 className="mb-0">Catatan</h5>
                    </div>
                    <div className="card-body">
                      <p><strong>Yang termasuk biaya Consumable:</strong></p>
                      <ul>
                        <li>Sedotan</li>
                        <li>Packaging</li>
                        <li>Paper bag</li>
                        <li>Tissue</li>
                        <li>Label produk</li>
                      </ul>
                      <hr />
                      <p><strong>Rumus Perhitungan:</strong></p>
                      <p>1. HPP = Total Biaya Produksi  Jumlah Pieces</p>
                      <p>2. Harga Dine In = HPP  (1 - Margin Profit)</p>
                      <p>3. Harga GoFood = Harga Dine In + Biaya Platform + Pajak</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <footer className="mt-4 mb-3 text-center text-muted">
        <p>Program Kalkulator HPP Resep Produk &copy; {new Date().getFullYear()} | Template berdasarkan model spreadsheet</p>
      </footer>
    </div>
  );
};

export default App;
