import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

const App = () => {
  // State untuk data kosong (tanpa default data)
  const [brand, setBrand] = useState('');
  const [targetCost, setTargetCost] = useState('');
  const [targetPieces, setTargetPieces] = useState('');
  const [recipeName, setRecipeName] = useState('');
  const [recipeCategory, setRecipeCategory] = useState('Cake');
  const [saveStatus, setSaveStatus] = useState('');

  // State untuk bahan baku kosong
  const [ingredients, setIngredients] = useState([
    { id: 1, name: '', usage: '', unit: 'gr', purchasePrice: '', purchaseUnit: '', purchaseUnitType: 'gram', targetCost: '' }
  ]);

  // State untuk consumable
  const [consumable, setConsumable] = useState({
    cost: '',
    quantity: '',
    unit: 'unit'
  });

  // State untuk persentase
  const [goFoodPercentage, setGoFoodPercentage] = useState(20);
  const [taxPercentage, setTaxPercentage] = useState(10);
  const [profitMargin, setProfitMargin] = useState(40);

  // State untuk history resep
  const [recipeHistory, setRecipeHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);

  // URL Google Apps Script (ganti dengan URL Anda)
  const GOOGLE_SCRIPT_URL = 'https://script.google.com/macros/s/YOUR_SCRIPT_ID/exec';

  // Fungsi untuk menghitung biaya per bahan
  const calculateIngredientCost = (ingredient) => {
    const usage = parseFloat(ingredient.usage) || 0;
    const purchaseUnit = parseFloat(ingredient.purchaseUnit) || 1;
    const purchasePrice = parseFloat(ingredient.purchasePrice) || 0;

    if (purchaseUnit > 0 && purchasePrice > 0) {
      return (usage / purchaseUnit) * purchasePrice;
    }
    return 0;
  };

  // Fungsi untuk menghitung total biaya bahan baku
  const calculateTotalMaterialCost = () => {
    return ingredients.reduce((total, ingredient) => {
      return total + calculateIngredientCost(ingredient);
    }, 0);
  };

  // Fungsi untuk menghitung total biaya produksi
  const calculateTotalProductionCost = () => {
    const materialCost = calculateTotalMaterialCost();
    const consumableCost = parseFloat(consumable.cost) || 0;
    return materialCost + consumableCost;
  };

  // Fungsi untuk menghitung HPP per piece
  const calculateHPPPerPiece = () => {
    const totalCost = calculateTotalProductionCost();
    const pieces = parseFloat(targetPieces) || 1;
    return pieces > 0 ? totalCost / pieces : 0;
  };

  // Fungsi untuk menghitung harga jual dine in
  const calculateDineInPrice = () => {
    const hpp = calculateHPPPerPiece();
    const margin = profitMargin / 100;
    return hpp / (1 - margin);
  };

  // Fungsi untuk menghitung laba kotor
  const calculateGrossProfit = () => {
    const dineInPrice = calculateDineInPrice();
    const hpp = calculateHPPPerPiece();
    return dineInPrice - hpp;
  };

  // Fungsi untuk menghitung biaya Go Food
  const calculateGoFoodCost = () => {
    const dineInPrice = calculateDineInPrice();
    return dineInPrice * (goFoodPercentage / 100);
  };

  // Fungsi untuk menghitung pajak restoran
  const calculateRestaurantTax = () => {
    const dineInPrice = calculateDineInPrice();
    return dineInPrice * (taxPercentage / 100);
  };

  // Fungsi untuk menghitung harga jual Go Food
  const calculateGoFoodPrice = () => {
    const dineInPrice = calculateDineInPrice();
    const goFoodCost = calculateGoFoodCost();
    const tax = calculateRestaurantTax();
    return dineInPrice + goFoodCost + tax;
  };

  // Fungsi untuk mengupdate data bahan
  const updateIngredient = (id, field, value) => {
    setIngredients(ingredients.map(ingredient =>
      ingredient.id === id ? { ...ingredient, [field]: value } : ingredient
    ));
  };

  // Fungsi untuk menambah bahan baru
  const addIngredient = () => {
    const newId = ingredients.length > 0 ? Math.max(...ingredients.map(i => i.id)) + 1 : 1;
    setIngredients([
      ...ingredients,
      {
        id: newId,
        name: '',
        usage: '',
        unit: 'gr',
        purchasePrice: '',
        purchaseUnit: '',
        purchaseUnitType: 'gram',
        targetCost: ''
      }
    ]);
  };

  // Fungsi untuk menghapus bahan
  const removeIngredient = (id) => {
    if (ingredients.length > 1) {
      setIngredients(ingredients.filter(ingredient => ingredient.id !== id));
    }
  };

  // Fungsi untuk reset semua data
  const resetAllData = () => {
    setBrand('');
    setTargetCost('');
    setTargetPieces('');
    setRecipeName('');
    setRecipeCategory('Cake');
    setIngredients([{ id: 1, name: '', usage: '', unit: 'gr', purchasePrice: '', purchaseUnit: '', purchaseUnitType: 'gram', targetCost: '' }]);
    setConsumable({ cost: '', quantity: '', unit: 'unit' });
    setGoFoodPercentage(20);
    setTaxPercentage(10);
    setProfitMargin(40);
    setSaveStatus('');
  };

  // Fungsi untuk menyimpan ke Google Spreadsheet
  const saveToGoogleSheet = async () => {
    if (!recipeName.trim()) {
      setSaveStatus('âš ï¸ Masukkan nama resep terlebih dahulu');
      setTimeout(() => setSaveStatus(''), 3000);
      return;
    }

    // Validasi data minimal
    const hasData = ingredients.some(ing => 
      ing.name.trim() && ing.usage && ing.purchasePrice
    );

    if (!hasData) {
      setSaveStatus('âš ï¸ Isi minimal satu bahan baku dengan lengkap');
      setTimeout(() => setSaveStatus(''), 3000);
      return;
    }

    setSaveStatus('â³ Menyimpan ke spreadsheet...');

    // Format tanggal
    const now = new Date();
    const dateString = now.toLocaleDateString('id-ID', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
    const timeString = now.toLocaleTimeString('id-ID', {
      hour: '2-digit',
      minute: '2-digit'
    });

    // Data utama
    const summaryData = {
      timestamp: `${dateString} ${timeString}`,
      recipeName: recipeName || 'Tanpa Nama',
      recipeCategory,
      brand: brand || '-',
      targetCost: parseFloat(targetCost) || 0,
      targetPieces: parseFloat(targetPieces) || 0,
      totalMaterialCost: calculateTotalMaterialCost(),
      packagingCost: parseFloat(consumable.cost) || 0,
      totalProductionCost: calculateTotalProductionCost(),
      hppPerPiece: calculateHPPPerPiece(),
      profitMargin: profitMargin,
      dineInPrice: calculateDineInPrice(),
      goFoodPercentage: goFoodPercentage,
      taxPercentage: taxPercentage,
      goFoodPrice: calculateGoFoodPrice(),
      grossProfit: calculateGrossProfit(),
      status: 'Saved'
    };

    // Data bahan baku (format untuk spreadsheet)
    const ingredientsData = ingredients.map(ing => ({
      recipeName: recipeName || 'Tanpa Nama',
      ingredientName: ing.name || '-',
      usage: parseFloat(ing.usage) || 0,
      unit: ing.unit,
      purchasePrice: parseFloat(ing.purchasePrice) || 0,
      purchaseUnit: parseFloat(ing.purchaseUnit) || 0,
      ingredientCost: calculateIngredientCost(ing)
    }));

    // Gabungkan data untuk dikirim
    const dataToSend = {
      action: 'saveRecipe',
      summary: summaryData,
      ingredients: ingredientsData
    };

    try {
      // Simpan ke localStorage sebagai backup
      const recipeToSave = {
        ...summaryData,
        ingredients: ingredientsData,
        consumable,
        timestamp: now.getTime()
      };

      const existingRecipes = JSON.parse(localStorage.getItem('recipeHistory') || '[]');
      existingRecipes.unshift(recipeToSave);
      localStorage.setItem('recipeHistory', JSON.stringify(existingRecipes.slice(0, 50))); // Simpan max 50 resep

      // Simpan ke Google Sheet (jika URL diisi)
      if (GOOGLE_SCRIPT_URL.includes('YOUR_SCRIPT_ID')) {
        console.log('Demo mode: Data disimpan di localStorage saja');
        console.log('Data yang akan dikirim ke Google Sheet:', dataToSend);
        setSaveStatus('âœ… Data disimpan di lokal (demo mode)');
      } else {
        const response = await fetch(GOOGLE_SCRIPT_URL, {
          method: 'POST',
          mode: 'no-cors',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(dataToSend)
        });

        // Karena no-cors, kita tidak bisa membaca response
        setSaveStatus('âœ… Data berhasil dikirim ke Google Sheet!');
      }

      // Tambahkan ke history state
      setRecipeHistory(existingRecipes);

      setTimeout(() => setSaveStatus(''), 5000);

    } catch (error) {
      console.error('Error saving to Google Sheet:', error);
      setSaveStatus('âŒ Gagal menyimpan. Data disimpan di lokal.');
      setTimeout(() => setSaveStatus(''), 5000);
    }
  };

  // Fungsi untuk memuat resep dari history
  const loadRecipeFromHistory = (recipe) => {
    setRecipeName(recipe.recipeName);
    setBrand(recipe.brand || '');
    setTargetCost(recipe.targetCost || '');
    setTargetPieces(recipe.targetPieces || '');
    setRecipeCategory(recipe.recipeCategory || 'Cake');
    setProfitMargin(recipe.profitMargin || 40);
    setGoFoodPercentage(recipe.goFoodPercentage || 20);
    setTaxPercentage(recipe.taxPercentage || 10);

    // Load consumable
    if (recipe.consumable) {
      setConsumable(recipe.consumable);
    }

    // Load ingredients
    if (recipe.ingredients && recipe.ingredients.length > 0) {
      const loadedIngredients = recipe.ingredients.map((ing, index) => ({
        id: index + 1,
        name: ing.ingredientName,
        usage: ing.usage,
        unit: ing.unit,
        purchasePrice: ing.purchasePrice,
        purchaseUnit: ing.purchaseUnit,
        purchaseUnitType: 'gram',
        targetCost: ''
      }));
      setIngredients(loadedIngredients);
    }

    setShowHistory(false);
    setSaveStatus('âœ… Resep dimuat dari history');
    setTimeout(() => setSaveStatus(''), 3000);
  };

  // Fungsi untuk menghapus resep dari history
  const deleteRecipeFromHistory = (index) => {
    const existingRecipes = JSON.parse(localStorage.getItem('recipeHistory') || '[]');
    existingRecipes.splice(index, 1);
    localStorage.setItem('recipeHistory', JSON.stringify(existingRecipes));
    setRecipeHistory(existingRecipes);
    setSaveStatus('ðŸ—‘ï¸ Resep dihapus dari history');
    setTimeout(() => setSaveStatus(''), 3000);
  };

  // Format angka ke Rupiah
  const formatRupiah = (number) => {
    if (isNaN(number) || number === 0) return 'Rp 0';
    return `Rp ${Math.round(number).toLocaleString('id-ID')}`;
  };

  // Load history saat component mount
  useEffect(() => {
    const savedRecipes = JSON.parse(localStorage.getItem('recipeHistory') || '[]');
    setRecipeHistory(savedRecipes);
  }, []);

  // Fungsi untuk export data ke CSV
  const exportToCSV = () => {
    const summaryData = {
      'Nama Resep': recipeName || 'Tanpa Nama',
      'Kategori': recipeCategory,
      'Brand/Produk': brand || '-',
      'Target Biaya': formatRupiah(targetCost),
      'Target Jumlah': targetPieces || '0',
      'Total Biaya Bahan': formatRupiah(calculateTotalMaterialCost()),
      'Biaya Packaging': formatRupiah(consumable.cost || 0),
      'Total Biaya Produksi': formatRupiah(calculateTotalProductionCost()),
      'HPP per Unit': formatRupiah(calculateHPPPerPiece()),
      'Margin Profit': `${profitMargin}%`,
      'Harga Dine In': formatRupiah(calculateDineInPrice()),
      'Biaya Platform': `${goFoodPercentage}%`,
      'Pajak': `${taxPercentage}%`,
      'Harga GoFood': formatRupiah(calculateGoFoodPrice()),
      'Laba Kotor': formatRupiah(calculateGrossProfit())
    };

    // Buat CSV content
    let csvContent = "Data Resep - Kalkulator HPP\n\n";

    // Summary
    csvContent += "SUMMARY\n";
    Object.entries(summaryData).forEach(([key, value]) => {
      csvContent += `${key},${value}\n`;
    });

    csvContent += "\nBAHAN BAKU\n";
    csvContent += "Nama Bahan,Jumlah Pakai,Satuan,Harga Beli,Satuan Beli,Biaya\n";

    ingredients.forEach(ing => {
      csvContent += `${ing.name || '-'},${ing.usage || '0'},${ing.unit},${formatRupiah(ing.purchasePrice || 0)},${ing.purchaseUnit || '0'},${formatRupiah(calculateIngredientCost(ing))}\n`;
    });

    // Buat blob dan download
    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement("a");
    const url = URL.createObjectURL(blob);
    link.setAttribute("href", url);
    link.setAttribute("download", `resep_${recipeName || 'hpp'}_${new Date().getTime()}.csv`);
    link.style.visibility = 'hidden';
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);

    setSaveStatus('ðŸ“¥ Data diexport ke CSV');
    setTimeout(() => setSaveStatus(''), 3000);
  };

  return (
    <div className="container mt-3">
      {/* Header */}
      <div className="header-section text-center mb-4">
        <h1 className="text-primary">ðŸ›’ Kalkulator HPP Produk</h1>
        <p className="text-muted">Hitung Harga Pokok Produksi dengan mudah untuk bisnis makanan Anda</p>

        {/* Status Save */}
        {saveStatus && (
          <div className={`alert ${saveStatus.includes('âœ…') ? 'alert-success' : saveStatus.includes('âš ï¸') ? 'alert-warning' : saveStatus.includes('âŒ') ? 'alert-danger' : 'alert-info'} alert-dismissible fade show`} style={{maxWidth: '500px', margin: '0 auto'}}>
            {saveStatus}
            <button type="button" className="btn-close" onClick={() => setSaveStatus('')}></button>
          </div>
        )}
      </div>

      {/* Info Box */}
      <div className="info-box mb-4 p-3 bg-light border rounded">
        <div className="d-flex justify-content-between align-items-center">
          <div>
            <h5>ðŸ“‹ Cara Menggunakan:</h5>
            <ol className="mb-0">
              <li>Isi nama resep dan kategori</li>
              <li>Masukkan data produk dan bahan baku</li>
              <li>Hitung dan simpan untuk penggunaan berikutnya</li>
              <li>Akses history untuk memuat resep sebelumnya</li>
            </ol>
          </div>
          <div>
            <button className="btn btn-outline-primary me-2" onClick={exportToCSV}>
              ðŸ“¥ Export CSV
            </button>
            <button className="btn btn-outline-secondary" onClick={() => setShowHistory(!showHistory)}>
              ðŸ“œ {showHistory ? 'Sembunyikan' : 'Lihat'} History
            </button>
          </div>
        </div>
      </div>

      {/* History Panel */}
      {showHistory && recipeHistory.length > 0 && (
        <div className="card shadow-sm mb-4">
          <div className="card-header bg-secondary text-white">
            <h5 className="mb-0">ðŸ“œ History Resep ({recipeHistory.length})</h5>
          </div>
          <div className="card-body">
            <div className="row">
              {recipeHistory.slice(0, 6).map((recipe, index) => (
                <div className="col-md-6 col-lg-4 mb-3" key={index}>
                  <div className="card h-100">
                    <div className="card-body">
                      <h6 className="card-title">{recipe.recipeName}</h6>
                      <p className="card-text small">
                        <span className="badge bg-info">{recipe.recipeCategory}</span><br/>
                        <strong>Brand:</strong> {recipe.brand || '-'}<br/>
                        <strong>HPP:</strong> {formatRupiah(recipe.hppPerPiece)}<br/>
                        <strong>Harga Jual:</strong> {formatRupiah(recipe.goFoodPrice)}<br/>
                        <small className="text-muted">
                          {recipe.timestamp || new Date(recipe.timestamp).toLocaleDateString()}
                        </small>
                      </p>
                    </div>
                    <div className="card-footer bg-transparent">
                      <div className="d-flex justify-content-between">
                        <button 
                          className="btn btn-sm btn-primary"
                          onClick={() => loadRecipeFromHistory(recipe)}
                        >
                          ðŸ“‚ Load
                        </button>
                        <button 
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => deleteRecipeFromHistory(index)}
                        >
                          ðŸ—‘ï¸
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
            {recipeHistory.length === 0 && (
              <p className="text-center text-muted">Belum ada resep yang disimpan</p>
            )}
          </div>
        </div>
      )}

      <div className="row">
        {/* Form Utama - Sisi Kiri */}
        <div className="col-lg-7">
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">ðŸ“ Data Resep & Produk</h5>
              <div>
                <button className="btn btn-light btn-sm me-2" onClick={resetAllData}>
                  ðŸ”„ Reset Semua
                </button>
                <button className="btn btn-success btn-sm" onClick={saveToGoogleSheet}>
                  ðŸ’¾ Simpan Resep
                </button>
              </div>
            </div>
            <div className="card-body">
              {/* Bagian Data Resep */}
              <div className="row mb-4">
                <div className="col-md-6 mb-3">
                  <label className="form-label">
                    <i className="bi bi-journal-text me-2"></i>Nama Resep
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    value={recipeName}
                    onChange={(e) => setRecipeName(e.target.value)}
                    placeholder="Contoh: Carrot Cake Premium"
                    required
                  />
                  <small className="text-muted">Wajib diisi untuk menyimpan resep</small>
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">
                    <i className="bi bi-tags me-2"></i>Kategori Resep
                  </label>
                  <select
                    className="form-select"
                    value={recipeCategory}
                    onChange={(e) => setRecipeCategory(e.target.value)}
                  >
                    <option value="Cake">Cake</option>
                    <option value="Pastry">Pastry</option>
                    <option value="Bread">Roti</option>
                    <option value="Cookies">Cookies</option>
                    <option value="Dessert">Dessert</option>
                    <option value="Beverage">Minuman</option>
                    <option value="Main Course">Main Course</option>
                    <option value="Other">Lainnya</option>
                  </select>
                </div>
              </div>

              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">
                    <i className="bi bi-shop me-2"></i>Nama Produk / Brand
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    value={brand}
                    onChange={(e) => setBrand(e.target.value)}
                    placeholder="Contoh: Carrot Cake Delight"
                  />
                </div>
                <div className="col-md-3 mb-3">
                  <label className="form-label">
                    <i className="bi bi-bullseye me-2"></i>Target Biaya per Unit
                  </label>
                  <div className="input-group">
                    <span className="input-group-text">Rp</span>
                    <input
                      type="number"
                      className="form-control"
                      value={targetCost}
                      onChange={(e) => setTargetCost(e.target.value)}
                      placeholder="Target"
                    />
                  </div>
                </div>
                <div className="col-md-3 mb-3">
                  <label className="form-label">
                    <i className="bi bi-box-seam me-2"></i>Jumlah Produksi
                  </label>
                  <div className="input-group">
                    <input
                      type="number"
                      className="form-control"
                      value={targetPieces}
                      onChange={(e) => setTargetPieces(e.target.value)}
                      placeholder="Jumlah"
                    />
                    <span className="input-group-text">pcs</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Bagian Bahan Baku */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-success text-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">ðŸ¥• Bahan Baku ({ingredients.length})</h5>
              <button className="btn btn-light btn-sm" onClick={addIngredient}>
                <i className="bi bi-plus-circle me-1"></i>Tambah
              </button>
            </div>
            <div className="card-body">
              {ingredients.map((ingredient, index) => (
                <div key={ingredient.id} className="ingredient-card mb-3 p-3 border rounded">
                  <div className="d-flex justify-content-between align-items-center mb-3">
                    <h6 className="mb-0">
                      <i className="bi bi-egg-fried me-2"></i>
                      Bahan {index + 1}: {ingredient.name || 'Belum diisi'}
                    </h6>
                    {ingredients.length > 1 && (
                      <button 
                        className="btn btn-outline-danger btn-sm"
                        onClick={() => removeIngredient(ingredient.id)}
                        title="Hapus bahan"
                      >
                        <i className="bi bi-trash"></i>
                      </button>
                    )}
                  </div>

                  <div className="row g-2">
                    <div className="col-md-4">
                      <label className="form-label small">Nama Bahan</label>
                      <input
                        type="text"
                        className="form-control form-control-sm"
                        value={ingredient.name}
                        onChange={(e) => updateIngredient(ingredient.id, 'name', e.target.value)}
                        placeholder="Contoh: Tepung Terigu"
                        required
                      />
                    </div>
                    <div className="col-md-3">
                      <label className="form-label small">Jumlah Pakai</label>
                      <div className="input-group input-group-sm">
                        <input
                          type="number"
                          className="form-control"
                          value={ingredient.usage}
                          onChange={(e) => updateIngredient(ingredient.id, 'usage', e.target.value)}
                          placeholder="360"
                          step="0.01"
                        />
                        <select
                          className="form-select"
                          style={{ width: '80px' }}
                          value={ingredient.unit}
                          onChange={(e) => updateIngredient(ingredient.id, 'unit', e.target.value)}
                        >
                          <option value="gr">gr</option>
                          <option value="ml">ml</option>
                          <option value="kg">kg</option>
                          <option value="pcs">pcs</option>
                          <option value="sdm">sdm</option>
                          <option value="sdt">sdt</option>
                        </select>
                      </div>
                    </div>
                    <div className="col-md-3">
                      <label className="form-label small">Harga Beli</label>
                      <div className="input-group input-group-sm">
                        <span className="input-group-text">Rp</span>
                        <input
                          type="number"
                          className="form-control"
                          value={ingredient.purchasePrice}
                          onChange={(e) => updateIngredient(ingredient.id, 'purchasePrice', e.target.value)}
                          placeholder="25000"
                          step="100"
                        />
                      </div>
                    </div>
                    <div className="col-md-2">
                      <label className="form-label small">Satuan Beli</label>
                      <input
                        type="number"
                        className="form-control form-control-sm"
                        value={ingredient.purchaseUnit}
                        onChange={(e) => updateIngredient(ingredient.id, 'purchaseUnit', e.target.value)}
                        placeholder="1000"
                        step="0.01"
                      />
                      <small className="text-muted">dalam {ingredient.unit}</small>
                    </div>
                  </div>

                  <div className="mt-2 d-flex justify-content-between align-items-center">
                    <small className="text-success">
                      <i className="bi bi-calculator me-1"></i>
                      Biaya bahan ini: <strong>{formatRupiah(calculateIngredientCost(ingredient))}</strong>
                    </small>
                    <small className="text-muted">
                      Per {ingredient.unit}: {formatRupiah(calculateIngredientCost(ingredient) / (parseFloat(ingredient.usage) || 1))}
                    </small>
                  </div>
                </div>
              ))}

              <div className="mt-3">
                <small className="text-muted">
                  <i className="bi bi-info-circle me-1"></i>
                  Total semua bahan: <strong>{formatRupiah(calculateTotalMaterialCost())}</strong>
                </small>
              </div>
            </div>
          </div>

          {/* Bagian Biaya Tambahan */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-warning">
              <h5 className="mb-0">ðŸ“¦ Biaya Tambahan & Margin</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">
                    <i className="bi bi-box me-2"></i>Biaya Packaging
                  </label>
                  <div className="input-group">
                    <span className="input-group-text">Rp</span>
                    <input
                      type="number"
                      className="form-control"
                      value={consumable.cost}
                      onChange={(e) => setConsumable({...consumable, cost: e.target.value})}
                      placeholder="Contoh: 5000"
                    />
                  </div>
                  <small className="text-muted">Untuk semua unit produksi</small>
                </div>
                <div className="col-md-6 mb-3">
                  <label className="form-label">
                    <i className="bi bi-cash-coin me-2"></i>Margin Profit
                  </label>
                  <div className="input-group">
                    <input
                      type="number"
                      className="form-control"
                      value={profitMargin}
                      onChange={(e) => setProfitMargin(e.target.value)}
                      placeholder="40"
                      min="0"
                      max="100"
                    />
                    <span className="input-group-text">%</span>
                  </div>
                  <small className="text-muted">Persentase laba yang diinginkan</small>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Panel Hasil - Sisi Kanan */}
        <div className="col-lg-5">
          {/* Ringkasan Hasil */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-info text-white">
              <h5 className="mb-0">ðŸ“Š Ringkasan Biaya</h5>
            </div>
            <div className="card-body">
              {recipeName && (
                <div className="alert alert-info py-2 mb-3">
                  <small>
                    <i className="bi bi-journal me-1"></i>
                    <strong>Resep:</strong> {recipeName} ({recipeCategory})
                  </small>
                </div>
              )}

              <div className="summary-item mb-3">
                <div className="d-flex justify-content-between mb-1">
                  <span>Total Biaya Bahan Baku:</span>
                  <span className="fw-bold">{formatRupiah(calculateTotalMaterialCost())}</span>
                </div>
                <div className="progress mb-2">
                  <div 
                    className="progress-bar bg-success" 
                    style={{ 
                      width: `${Math.min(100, (calculateTotalMaterialCost() / (calculateTotalProductionCost() || 1)) * 100)}%` 
                    }}
                  ></div>
                </div>
              </div>

              <div className="summary-item mb-3">
                <div className="d-flex justify-content-between mb-1">
                  <span>Biaya Packaging:</span>
                  <span className="fw-bold">{formatRupiah(parseFloat(consumable.cost) || 0)}</span>
                </div>
                <div className="progress mb-2">
                  <div 
                    className="progress-bar bg-warning" 
                    style={{ 
                      width: `${Math.min(100, ((parseFloat(consumable.cost) || 0) / (calculateTotalProductionCost() || 1)) * 100)}%` 
                    }}
                  ></div>
                </div>
              </div>

              <div className="summary-item mb-3">
                <div className="d-flex justify-content-between mb-1">
                  <span>Total Biaya Produksi:</span>
                  <span className="fw-bold text-primary">{formatRupiah(calculateTotalProductionCost())}</span>
                </div>
                <div className="progress mb-2">
                  <div 
                    className="progress-bar bg-primary" 
                    style={{ width: '100%' }}
                  ></div>
                </div>
              </div>

              <hr />

              <div className="result-box p-3 bg-light rounded mb-3">
                <div className="d-flex justify-content-between align-items-center mb-2">
                  <h6 className="mb-0">HPP per Unit:</h6>
                  <h5 className="mb-0 text-success">{formatRupiah(calculateHPPPerPiece())}</h5>
                </div>
                <small className="text-muted">
                  Harga Pokok Produksi untuk 1 unit produk ({targetPieces || 1} pcs)
                </small>
              </div>

              {targetCost && (
                <div className="result-box p-3 bg-light rounded">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <h6 className="mb-0">Target vs Aktual:</h6>
                    <h5 className="mb-0" style={{
                      color: (parseFloat(targetCost) || 0) - calculateHPPPerPiece() >= 0 ? 'green' : 'red'
                    }}>
                      {formatRupiah((parseFloat(targetCost) || 0) - calculateHPPPerPiece())}
                    </h5>
                  </div>
                  <small className="text-muted">
                    {((parseFloat(targetCost) || 0) - calculateHPPPerPiece()) >= 0 
                      ? 'âœ… Dibawah target (menguntungkan)' 
                      : 'âš ï¸ Di atas target (perlu penyesuaian)'}
                  </small>
                </div>
              )}
            </div>
          </div>

          {/* Kalkulator Harga Jual */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-purple text-white">
              <h5 className="mb-0">ðŸ’° Kalkulator Harga Jual</h5>
            </div>
            <div className="card-body">
              <div className="row mb-3">
                <div className="col-md-6">
                  <label className="form-label small">Biaya Platform</label>
                  <div className="input-group input-group-sm mb-2">
                    <input
                      type="number"
                      className="form-control"
                      value={goFoodPercentage}
                      onChange={(e) => setGoFoodPercentage(e.target.value)}
                      placeholder="20"
                      min="0"
                      max="100"
                    />
                    <span className="input-group-text">%</span>
                  </div>
                </div>
                <div className="col-md-6">
                  <label className="form-label small">Pajak Restoran</label>
                  <div className="input-group input-group-sm mb-2">
                    <input
                      type="number"
                      className="form-control"
                      value={taxPercentage}
                      onChange={(e) => setTaxPercentage(e.target.value)}
                      placeholder="10"
                      min="0"
                      max="100"
                    />
                    <span className="input-group-text">%</span>
                  </div>
                </div>
              </div>

              <div className="price-results">
                <div className="price-item mb-2 p-2 bg-light rounded">
                  <div className="d-flex justify-content-between">
                    <span>Harga Dine In:</span>
                    <span className="fw-bold">{formatRupiah(calculateDineInPrice())}</span>
                  </div>
                  <small className="text-muted">Termasuk margin {profitMargin}%</small>
                </div>

                <div className="price-item mb-2 p-2 bg-light rounded">
                  <div className="d-flex justify-content-between">
                    <span>Biaya Platform ({goFoodPercentage}%):</span>
                    <span className="text-warning">{formatRupiah(calculateGoFoodCost())}</span>
                  </div>
                </div>

                <div className="price-item mb-2 p-2 bg-light rounded">
                  <div className="d-flex justify-content-between">
                    <span>Pajak Restoran ({taxPercentage}%):</span>
                    <span className="text-warning">{formatRupiah(calculateRestaurantTax())}</span>
                  </div>
                </div>

                <div className="price-item p-2 bg-success text-white rounded mt-3">
                  <div className="d-flex justify-content-between">
                    <span><strong>Harga GoFood Final:</strong></span>
                    <span><strong>{formatRupiah(calculateGoFoodPrice())}</strong></span>
                  </div>
                </div>
              </div>

              <div className="mt-3 row">
                <div className="col-md-6">
                  <small className="text-muted">
                    <i className="bi bi-graph-up me-1"></i>
                    Laba kotor: {formatRupiah(calculateGrossProfit())}
                  </small>
                </div>
                <div className="col-md-6 text-end">
                  <small className="text-muted">
                    Margin: {((calculateGrossProfit() / calculateDineInPrice()) * 100).toFixed(1)}%
                  </small>
                </div>
              </div>
            </div>
          </div>

          {/* Info Google Sheet */}
          <div className="card shadow-sm">
            <div className="card-header bg-light">
              <h5 className="mb-0">ðŸ“Š Simpan ke Spreadsheet</h5>
            </div>
            <div className="card-body">
              <div className="mb-3">
                <h6>Fitur Penyimpanan:</h6>
                <ul className="small mb-3">
                  <li>Simpan data resep ke lokal browser</li>
                  <li>Export ke file CSV untuk backup</li>
                  <li>Siap integrasi dengan Google Sheets</li>
                </ul>
              </div>

              <div className="alert alert-warning small">
                <strong>Untuk Google Sheets:</strong>
                <ol className="mb-0 mt-2">
                  <li>Buat Google Apps Script baru</li>
                  <li>Deploy sebagai Web App</li>
                  <li>Ganti URL di kode dengan URL deploy Anda</li>
                  <li>Data akan dikirim ke spreadsheet</li>
                </ol>
              </div>

              <div className="d-grid gap-2">
                <button 
                  className="btn btn-primary" 
                  onClick={saveToGoogleSheet}
                  disabled={!recipeName.trim()}
                >
                  {!recipeName.trim() ? 'âš ï¸ Isi Nama Resep' : 'ðŸ’¾ Simpan Resep'}
                </button>
                <button 
                  className="btn btn-outline-secondary"
                  onClick={exportToCSV}
                >
                  ðŸ“¥ Export ke CSV
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="mt-4 mb-3 text-center text-muted">
        <p className="small">
          <i className="bi bi-calculator me-1"></i>
          Kalkulator HPP Produk Â© {new Date().getFullYear()} | 
          Total resep tersimpan: {recipeHistory.length}
        </p>
      </footer>
    </div>
  );
};

export default App;
