      // Hapus duplikat berdasarkan recipe_name
      const filteredCache = existingCache.filter(
        (item) => item.recipe_name !== recipeData.recipe_name,
      );

      filteredCache.unshift(cacheData);
      localStorage.setItem(
        "hpp_cache",
        JSON.stringify(filteredCache.slice(0, 20)),
      );
      setRecipeHistory(filteredCache.slice(0, 20));
    } catch (error) {
      console.error("Cache error:", error);
    }
  };

  const loadFromCache = (recipe) => {
