                          <th>Actions</th>
                        </tr>
                      </thead>
                      <tbody>
                        {savedMenus.map((menu, index) => (
                          <tr key={index}>
                            <td>
                              <small className="text-muted">{menu.menu_id}</small>
                            </td>
                            <td>
                              <strong>{menu.nama_menu}</strong>
