                            disabled={loading}>
                            <option value="BAHAN_UTAMA">Main Ingredient</option>
                            <option value="BAHAN_TAMBAHAN">Additional</option>
                            <option value="BUMBU">Seasoning</option>
                            <option value="KEMASAN">Packaging</option>
