import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// GANTI DENGAN URL DEPLOYMENT ANDA
const GOOGLE_SCRIPT_URL = 'https://script.google.com/macros/s/AKfycbygIAnjlz9QvDfNZnoeL-kQ-g_3ogO_lRizJJGnCFFx/dev';

const CATEGORIES = {
  'Makanan': ['Kids Menu', 'Appetizer', 'Main Course', 'Dessert', 'Breakfast Menu', 'Veggies', 'Others'],
  'Minuman': ['Signature', 'Espresso Based', 'Single Origin', 'Frappuccino', 'Milkshake', 'Ice Cream', 'Non Coffee']
};

const App = () => {
  // States
  const [brand, setBrand] = useState('');
  const [targetCost, setTargetCost] = useState('');
  const [targetPieces, setTargetPieces] = useState('');
  const [recipeName, setRecipeName] = useState('');
  const [recipeCategory, setRecipeCategory] = useState('Makanan');
  const [recipeSubCategory, setRecipeSubCategory] = useState('Main Course');
  const [saveStatus, setSaveStatus] = useState({ type: '', message: '' });
  const [isLoading, setIsLoading] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('checking');
  const [lastCheck, setLastCheck] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editRecipeId, setEditRecipeId] = useState(null);

  const [ingredients, setIngredients] = useState([{ 
    id: 1, 
    name: '', 
    usage: '', 
    unit: 'gr', 
    purchasePrice: '', 
    purchaseUnit: '',
    purchaseUnitType: 'gr'
  }]);

  const [consumable, setConsumable] = useState({ 
    name: 'Packaging', 
    cost: '', 
    quantity: '1', 
    unit: 'unit'
  });

  const [goFoodPercentage, setGoFoodPercentage] = useState(20);
  const [taxPercentage, setTaxPercentage] = useState(10);
  const [profitMargin, setProfitMargin] = useState(40);
  const [recipeHistory, setRecipeHistory] = useState([]);
  const [recipeNotes, setRecipeNotes] = useState('');

  // Initialize
  useEffect(() => {
    testConnection();
    const cached = JSON.parse(localStorage.getItem('hpp_cache') || '[]');
    setRecipeHistory(cached);
  }, []);

  // ===== UTILITY FUNCTIONS =====
  const formatRupiah = (number) => {
    if (isNaN(number) || number === 0) return 'Rp 0';
    return `Rp ${Math.round(number).toLocaleString('id-ID')}`;
  };

  const calculateIngredientCost = (ing) => {
    const usage = parseFloat(ing.usage) || 0;
    const purchaseUnit = parseFloat(ing.purchaseUnit) || 1;
    const purchasePrice = parseFloat(ing.purchasePrice) || 0;
    return purchaseUnit > 0 ? (usage / purchaseUnit) * purchasePrice : 0;
  };

  const calculateTotalMaterialCost = () => {
    return ingredients.reduce((total, ing) => total + calculateIngredientCost(ing), 0);
  };

  const calculateTotalProductionCost = () => {
    return calculateTotalMaterialCost() + (parseFloat(consumable.cost) || 0);
  };

  const calculateHPPPerPiece = () => {
    const total = calculateTotalProductionCost();
    const pieces = parseFloat(targetPieces) || 1;
    return pieces > 0 ? total / pieces : 0;
  };

  const calculateDineInPrice = () => {
    const hpp = calculateHPPPerPiece();
    const margin = profitMargin / 100;
    return margin > 0 ? hpp / (1 - margin) : hpp;
  };

  // ===== INGREDIENT MANAGEMENT =====
  const updateIngredient = (id, field, value) => {
    setIngredients(ingredients.map(ingredient =>
      ingredient.id === id ? { ...ingredient, [field]: value } : ingredient
    ));
  };

  const addIngredient = () => {
    const newId = ingredients.length > 0 ? Math.max(...ingredients.map(i => i.id)) + 1 : 1;
    setIngredients([...ingredients, { 
      id: newId, 
      name: '', 
      usage: '', 
      unit: 'gr', 
      purchasePrice: '', 
      purchaseUnit: '',
      purchaseUnitType: 'gr'
    }]);
  };

  const removeIngredient = (id) => {
    if (ingredients.length > 1) {
      setIngredients(ingredients.filter(ingredient => ingredient.id !== id));
    }
  };

  // ===== CONNECTION TEST =====
  const testConnection = async () => {
    try {
      setConnectionStatus('checking');
      setSaveStatus({ type: 'loading', message: 'ðŸ”„ Testing connection to Google Sheets...' });

      // Gunakan fetch dengan timeout
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000);

      const response = await fetch(`${GOOGLE_SCRIPT_URL}?action=ping&t=${Date.now()}`, {
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          setConnectionStatus('connected');
          setLastCheck(new Date());
          setSaveStatus({ type: 'success', message: 'âœ… Connected to Google Sheets!' });
          return true;
        }
      }

      throw new Error('Connection failed');

    } catch (error) {
      console.error('Connection test error:', error);
      setConnectionStatus('error');
      setLastCheck(new Date());
      setSaveStatus({ 
        type: 'warning', 
        message: 'âš ï¸ Cannot connect to Google Sheets. Working in offline mode.' 
      });
      return false;
    }
  };

  // ===== SIMPLE SAVE FUNCTION =====
  const saveToGoogleSheets = async () => {
    // Validasi
    if (!recipeName.trim()) {
      setSaveStatus({ type: 'error', message: 'âš ï¸ Please enter recipe name' });
      return;
    }

    setIsLoading(true);
    setSaveStatus({ type: 'loading', message: 'ðŸ“¤ Saving to Google Sheets...' });

    try {
      // Prepare data sederhana
      const recipeData = {
        action: 'save_recipe',
        recipe_name: recipeName.trim(),
        recipe_category: recipeCategory,
        recipe_subcategory: recipeSubCategory,
        brand: brand.trim() || '-',
        target_cost: parseFloat(targetCost) || 0,
        target_pieces: parseFloat(targetPieces) || 0,
        hpp_per_piece: calculateHPPPerPiece(),
        profit_margin: profitMargin,
        dine_in_price: calculateDineInPrice(),
        ingredients: ingredients.map(ing => ({
          name: ing.name.trim(),
          usage: ing.usage,
          unit: ing.unit,
          purchasePrice: ing.purchasePrice,
          purchaseUnit: ing.purchaseUnit
        })),
        packaging: consumable,
        notes: recipeNotes,
        timestamp: new Date().toLocaleString('id-ID')
      };

      // Kirim dengan timeout
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      const response = await fetch(GOOGLE_SCRIPT_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(recipeData),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const result = await response.json();

      if (result.success) {
        // Simpan ke cache lokal juga
        saveToLocalCache(recipeData);

        setSaveStatus({ 
          type: 'success', 
          message: `âœ… Recipe "${recipeName}" saved successfully!` 
        });

        // Reset form setelah 2 detik
        setTimeout(() => {
          resetForm();
          setSaveStatus({ type: 'info', message: 'ðŸ“ Form cleared. Ready for new recipe!' });
        }, 2000);

      } else {
        throw new Error(result.message || 'Save failed');
      }

      setIsLoading(false);

    } catch (error) {
      console.error('Save error:', error);
      setIsLoading(false);

      // Fallback: save to local cache
      const recipeData = {
        recipe_name: recipeName.trim(),
        recipe_category: recipeCategory,
        recipe_subcategory: recipeSubCategory,
        hpp_per_piece: calculateHPPPerPiece(),
        timestamp: new Date().toLocaleString('id-ID'),
        status: 'CACHED_OFFLINE'
      };

      saveToLocalCache(recipeData);

      setSaveStatus({ 
        type: 'warning', 
        message: `âš ï¸ Saved to local cache. Please check: 
        1. Google Apps Script URL is correct
        2. Script is deployed as Web App
        3. Who has access: "Anyone"
        4. Internet connection is stable` 
      });
    }
  };

  // ===== LOCAL CACHE FUNCTIONS =====
  const saveToLocalCache = (recipeData) => {
    try {
      const cacheItem = {
        ...recipeData,
        cached_at: new Date().toISOString(),
        local_id: 'LOCAL_' + Date.now()
      };

      const existingCache = JSON.parse(localStorage.getItem('hpp_cache') || '[]');
      existingCache.unshift(cacheItem);

      // Simpan maksimal 50 item
      localStorage.setItem('hpp_cache', JSON.stringify(existingCache.slice(0, 50)));
      setRecipeHistory(existingCache.slice(0, 20));

    } catch (error) {
      console.error('Cache save error:', error);
    }
  };

  const loadFromCache = (recipe) => {
    setRecipeName(recipe.recipe_name || '');
    setRecipeCategory(recipe.recipe_category || 'Makanan');
    setRecipeSubCategory(recipe.recipe_subcategory || 'Main Course');
    setBrand(recipe.brand || '');
    setTargetCost(recipe.target_cost?.toString() || '');
    setTargetPieces(recipe.target_pieces?.toString() || '');
    setProfitMargin(recipe.profit_margin || 40);
    setRecipeNotes(recipe.notes || '');

    if (recipe.ingredients && Array.isArray(recipe.ingredients)) {
      const formattedIngredients = recipe.ingredients.map((ing, index) => ({
        id: index + 1,
        name: ing.name || '',
        usage: ing.usage?.toString() || '',
        unit: ing.unit || 'gr',
        purchasePrice: ing.purchasePrice?.toString() || '',
        purchaseUnit: ing.purchaseUnit?.toString() || '1',
        purchaseUnitType: ing.purchaseUnitType || 'gr'
      }));
      setIngredients(formattedIngredients.length > 0 ? formattedIngredients : [{ 
        id: 1, 
        name: '', 
        usage: '', 
        unit: 'gr', 
        purchasePrice: '', 
        purchaseUnit: '',
        purchaseUnitType: 'gr'
      }]);
    }

    if (recipe.packaging) {
      setConsumable({
        name: recipe.packaging.name || 'Packaging',
        cost: recipe.packaging.cost?.toString() || '',
        quantity: recipe.packaging.quantity?.toString() || '1',
        unit: recipe.packaging.unit || 'unit'
      });
    }

    setSaveStatus({ type: 'info', message: 'ðŸ“‚ Recipe loaded from cache' });
  };

  // ===== RESET FORM =====
  const resetForm = () => {
    setRecipeName('');
    setBrand('');
    setTargetCost('');
    setTargetPieces('');
    setIngredients([{ 
      id: 1, 
      name: '', 
      usage: '', 
      unit: 'gr', 
      purchasePrice: '', 
      purchaseUnit: '',
      purchaseUnitType: 'gr'
    }]);
    setConsumable({ 
      name: 'Packaging', 
      cost: '', 
      quantity: '1', 
      unit: 'unit'
    });
    setRecipeNotes('');
    setEditMode(false);
    setEditRecipeId(null);
  };

  // ===== RENDER =====
  return (
    <div className="container mt-3">
      {/* Header */}
      <div className="header-section text-center mb-4">
        <h1 className="text-primary">ðŸš€ HPP Calculator</h1>
        <p className="text-muted">Save recipes to Google Sheets</p>

        <div className="d-flex justify-content-center align-items-center mb-3">
          <div className={`badge ${connectionStatus === 'connected' ? 'bg-success' : 
                           connectionStatus === 'error' ? 'bg-danger' : 'bg-warning'} me-2`}
               style={{ fontSize: '0.9rem', padding: '6px 12px' }}>
            {connectionStatus === 'connected' ? 'âœ… CONNECTED' : 
             connectionStatus === 'error' ? 'âŒ OFFLINE' : 'âŒ› CHECKING...'}
          </div>

          <button className="btn btn-sm btn-outline-secondary ms-2" onClick={testConnection} disabled={isLoading}>
            Test Connection
          </button>

          {lastCheck && <small className="text-muted ms-2">Last: {lastCheck.toLocaleTimeString()}</small>}
        </div>

        {saveStatus.message && (
          <div className={`alert ${saveStatus.type === 'success' ? 'alert-success' : 
                           saveStatus.type === 'error' ? 'alert-danger' : 
                           saveStatus.type === 'loading' ? 'alert-info' : 
                           'alert-warning'} alert-dismissible fade show`} 
               style={{maxWidth: '800px', margin: '0 auto'}}>
            <div className="d-flex justify-content-between align-items-center">
              <div>{saveStatus.message}</div>
              <button type="button" className="btn-close" onClick={() => setSaveStatus({ type: '', message: '' })}></button>
            </div>
          </div>
        )}
      </div>

      {/* Main Form */}
      <div className="row">
        <div className="col-lg-8">
          {/* Recipe Info Card */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-primary text-white">
              <h5 className="mb-0">ðŸ“ Recipe Information</h5>
            </div>
            <div className="card-body">
              <div className="row mb-3">
                <div className="col-md-6">
                  <label className="form-label">Recipe Name *</label>
                  <input type="text" className="form-control" value={recipeName} 
                    onChange={(e) => setRecipeName(e.target.value)} 
                    placeholder="e.g., Spaghetti Carbonara" 
                    disabled={isLoading} required />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Category</label>
                  <select className="form-select mb-2" value={recipeCategory} 
                    onChange={(e) => setRecipeCategory(e.target.value)} 
                    disabled={isLoading}>
                    <option value="Makanan">Food</option>
                    <option value="Minuman">Drink</option>
                  </select>
                  <select className="form-select" value={recipeSubCategory} 
                    onChange={(e) => setRecipeSubCategory(e.target.value)} 
                    disabled={isLoading}>
                    {CATEGORIES[recipeCategory]?.map(sub => (
                      <option key={sub} value={sub}>{sub}</option>
                    ))}
                  </select>
                </div>
              </div>

              <div className="row mb-3">
                <div className="col-md-6">
                  <label className="form-label">Brand / Product Name</label>
                  <input type="text" className="form-control" value={brand} 
                    onChange={(e) => setBrand(e.target.value)} 
                    placeholder="e.g., Signature Dish" 
                    disabled={isLoading} />
                </div>
                <div className="col-md-3">
                  <label className="form-label">Target Cost</label>
                  <div className="input-group">
                    <span className="input-group-text">Rp</span>
                    <input type="number" className="form-control" value={targetCost} 
                      onChange={(e) => setTargetCost(e.target.value)} 
                      placeholder="Target" min="0" 
                      disabled={isLoading} />
                  </div>
                </div>
                <div className="col-md-3">
                  <label className="form-label">Production Qty</label>
                  <div className="input-group">
                    <input type="number" className="form-control" value={targetPieces} 
                      onChange={(e) => setTargetPieces(e.target.value)} 
                      placeholder="Qty" min="1" 
                      disabled={isLoading} />
                    <span className="input-group-text">pcs</span>
                  </div>
                </div>
              </div>

              <div className="mb-3">
                <label className="form-label">Notes</label>
                <textarea className="form-control" rows="2" value={recipeNotes}
                  onChange={(e) => setRecipeNotes(e.target.value)} 
                  placeholder="Additional notes about this recipe..." 
                  disabled={isLoading} />
              </div>
            </div>
          </div>

          {/* Ingredients Card */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-success text-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">ðŸ¥• Ingredients ({ingredients.length})</h5>
              <button className="btn btn-light btn-sm" onClick={addIngredient} disabled={isLoading}>
                + Add Ingredient
              </button>
            </div>
            <div className="card-body">
              {ingredients.map((ingredient, index) => (
                <div key={ingredient.id} className="ingredient-card mb-3 p-3 border rounded">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <h6 className="mb-0">
                      <span className="badge bg-secondary me-2">{index + 1}</span>
                      {ingredient.name || 'New Ingredient'}
                    </h6>
                    {ingredients.length > 1 && (
                      <button className="btn btn-outline-danger btn-sm" 
                        onClick={() => removeIngredient(ingredient.id)} 
                        disabled={isLoading}>
                        âœ•
                      </button>
                    )}
                  </div>

                  <div className="row g-2">
                    <div className="col-md-4">
                      <label className="form-label small">Ingredient Name *</label>
                      <input type="text" className="form-control form-control-sm" 
                        value={ingredient.name} 
                        onChange={(e) => updateIngredient(ingredient.id, 'name', e.target.value)} 
                        placeholder="e.g., Flour" 
                        disabled={isLoading} required />
                    </div>

                    <div className="col-md-3">
                      <label className="form-label small">Usage Amount *</label>
                      <div className="input-group input-group-sm">
                        <input type="number" className="form-control" 
                          value={ingredient.usage} 
                          onChange={(e) => updateIngredient(ingredient.id, 'usage', e.target.value)} 
                          placeholder="360" step="0.01" min="0" 
                          disabled={isLoading} required />
                        <select className="form-select" style={{ width: '80px' }} 
                          value={ingredient.unit} 
                          onChange={(e) => updateIngredient(ingredient.id, 'unit', e.target.value)} 
                          disabled={isLoading}>
                          <option value="gr">gr</option>
                          <option value="ml">ml</option>
                          <option value="kg">kg</option>
                          <option value="pcs">pcs</option>
                          <option value="sdm">tbsp</option>
                          <option value="sdt">tsp</option>
                        </select>
                      </div>
                    </div>

                    <div className="col-md-3">
                      <label className="form-label small">Purchase Price *</label>
                      <div className="input-group input-group-sm">
                        <span className="input-group-text">Rp</span>
                        <input type="number" className="form-control" 
                          value={ingredient.purchasePrice} 
                          onChange={(e) => updateIngredient(ingredient.id, 'purchasePrice', e.target.value)} 
                          placeholder="25000" step="100" min="0" 
                          disabled={isLoading} required />
                      </div>
                    </div>

                    <div className="col-md-2">
                      <label className="form-label small">Purchase Unit *</label>
                      <input type="number" className="form-control form-control-sm" 
                        value={ingredient.purchaseUnit} 
                        onChange={(e) => updateIngredient(ingredient.id, 'purchaseUnit', e.target.value)} 
                        placeholder="1000" step="0.01" min="0.01" 
                        disabled={isLoading} required />
                      <small className="text-muted">in {ingredient.unit}</small>
                    </div>
                  </div>

                  <div className="mt-2">
                    <small className="text-success">
                      Cost for this recipe: <strong>{formatRupiah(calculateIngredientCost(ingredient))}</strong>
                    </small>
                  </div>
                </div>
              ))}

              <div className="mt-3">
                <small className="text-muted">
                  Total ingredients cost: <strong>{formatRupiah(calculateTotalMaterialCost())}</strong>
                </small>
              </div>
            </div>
          </div>

          {/* Packaging Card */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-warning">
              <h5 className="mb-0">ðŸ“¦ Packaging Cost</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label className="form-label">Packaging Name</label>
                  <input type="text" className="form-control" value={consumable.name} 
                    onChange={(e) => setConsumable({...consumable, name: e.target.value})} 
                    placeholder="e.g., Food Box" 
                    disabled={isLoading} />
                </div>
                <div className="col-md-3 mb-3">
                  <label className="form-label">Total Cost</label>
                  <div className="input-group">
                    <span className="input-group-text">Rp</span>
                    <input type="number" className="form-control" value={consumable.cost} 
                      onChange={(e) => setConsumable({...consumable, cost: e.target.value})} 
                      placeholder="5000" min="0" 
                      disabled={isLoading} />
                  </div>
                </div>
                <div className="col-md-3 mb-3">
                  <label className="form-label">Quantity</label>
                  <input type="number" className="form-control" value={consumable.quantity} 
                    onChange={(e) => setConsumable({...consumable, quantity: e.target.value})} 
                    placeholder="1" min="1" 
                    disabled={isLoading} />
                </div>
              </div>

              <div className="row">
                <div className="col-md-6">
                  <label className="form-label">Profit Margin (%)</label>
                  <div className="input-group">
                    <input type="number" className="form-control" value={profitMargin} 
                      onChange={(e) => setProfitMargin(e.target.value)} 
                      placeholder="40" min="0" max="100" 
                      disabled={isLoading} />
                    <span className="input-group-text">%</span>
                  </div>
                </div>
                <div className="col-md-6">
                  <div className="alert alert-light mt-4">
                    <small>
                      Packaging cost per unit: <strong>
                        {formatRupiah((parseFloat(consumable.cost) || 0) / (parseFloat(consumable.quantity) || 1))}
                      </strong>
                    </small>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Right Column - Results & Save */}
        <div className="col-lg-4">
          {/* Cost Summary */}
          <div className="card shadow-sm mb-4">
            <div className="card-header bg-info text-white">
              <h5 className="mb-0">ðŸ“Š Cost Summary</h5>
            </div>
            <div className="card-body">
              <div className="summary-item mb-3">
                <div className="d-flex justify-content-between mb-1">
                  <span>Total Ingredients:</span>
                  <span className="fw-bold text-success">{formatRupiah(calculateTotalMaterialCost())}</span>
                </div>
                <small className="text-muted">{ingredients.length} items</small>
              </div>

              <div className="summary-item mb-3">
                <div className="d-flex justify-content-between mb-1">
                  <span>Packaging Cost:</span>
                  <span className="fw-bold text-warning">{formatRupiah(parseFloat(consumable.cost) || 0)}</span>
                </div>
              </div>

              <div className="summary-item mb-3 p-2 bg-light rounded">
                <div className="d-flex justify-content-between mb-1">
                  <span>Total Production Cost:</span>
                  <span className="fw-bold text-primary">{formatRupiah(calculateTotalProductionCost())}</span>
                </div>
                <small className="text-muted">For {targetPieces || 1} units</small>
              </div>

              <hr />

              <div className="result-box p-3 bg-success text-white rounded mb-3">
                <div className="d-flex justify-content-between align-items-center mb-1">
                  <h6 className="mb-0">HPP per Unit:</h6>
                  <h4 className="mb-0">{formatRupiah(calculateHPPPerPiece())}</h4>
                </div>
                <small>Cost per serving</small>
              </div>

              {targetCost && (
                <div className="result-box p-3 bg-light rounded">
                  <div className="d-flex justify-content-between align-items-center mb-1">
                    <h6 className="mb-0">Target Difference:</h6>
                    <h5 className="mb-0" style={{color: (parseFloat(targetCost) || 0) >= calculateHPPPerPiece() ? 'green' : 'red'}}>
                      {formatRupiah((parseFloat(targetCost) || 0) - calculateHPPPerPiece())}
                    </h5>
                  </div>
                  <small className="text-muted">
                    {(parseFloat(targetCost) || 0) >= calculateHPPPerPiece() ? 'âœ… Profitable' : 'âš ï¸ Needs adjustment'}
                  </small>
                </div>
              )}
            </div>
          </div>

          {/* Save Card */}
          <div className="card shadow-sm">
            <div className="card-header bg-success text-white">
              <h5 className="mb-0">ðŸ’¾ Save Recipe</h5>
            </div>
            <div className="card-body">
              <div className="mb-3">
                <h6>Data to be saved:</h6>
                <div className="small">
                  <div className="d-flex align-items-center mb-1">
                    <span className="badge bg-info me-2">ðŸ“‹</span>
                    <span>Recipe Information</span>
                  </div>
                  <div className="d-flex align-items-center mb-1">
                    <span className="badge bg-success me-2">ðŸ¥•</span>
                    <span>{ingredients.length} Ingredients</span>
                  </div>
                  <div className="d-flex align-items-center mb-1">
                    <span className="badge bg-warning me-2">ðŸ“¦</span>
                    <span>Packaging Data</span>
                  </div>
                  <div className="d-flex align-items-center mb-1">
                    <span className="badge bg-purple me-2">ðŸ’°</span>
                    <span>Pricing & Calculations</span>
                  </div>
                </div>
              </div>

              <div className="alert alert-info small mb-3">
                <strong>Connection Status:</strong>
                <p className="mb-0 mt-1">
                  {connectionStatus === 'connected' 
                    ? 'âœ… Ready to save to Google Sheets' 
                    : 'âš ï¸ Will save to local cache first'}
                </p>
              </div>

              <div className="d-grid gap-2">
                <button className="btn btn-success btn-lg"
                  onClick={saveToGoogleSheets}
                  disabled={isLoading || !recipeName.trim()}>
                  {isLoading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2"></span>
                      Saving...
                    </>
                  ) : (
                    <>
                      <i className="bi bi-save me-2"></i>
                      Save Recipe to Google Sheets
                    </>
                  )}
                </button>

                <button className="btn btn-outline-primary"
                  onClick={() => {
                    const cached = JSON.parse(localStorage.getItem('hpp_cache') || '[]');
                    if (cached.length > 0) {
                      loadFromCache(cached[0]);
                    } else {
                      setSaveStatus({ type: 'info', message: 'No cached recipes found' });
                    }
                  }}
                  disabled={isLoading}>
                  Load Last Recipe
                </button>

                <button className="btn btn-outline-secondary"
                  onClick={resetForm}
                  disabled={isLoading}>
                  Clear Form
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="mt-4 mb-3 text-center">
        <p className="small text-muted">
          Status: <span className={`badge ${connectionStatus === 'connected' ? 'bg-success' : 'bg-warning'}`}>
            {connectionStatus === 'connected' ? 'CONNECTED' : 'OFFLINE'}
          </span>
          <span className="ms-3">Recipes in cache: {recipeHistory.length}</span>
          <span className="ms-3">Ingredients: {ingredients.length}</span>
        </p>
        <p className="small text-muted">
          HPP Calculator v1.0 | Google Sheets Integration | Offline Support
        </p>
      </footer>
    </div>
  );
};

export default App;
