import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

// PRODUCTION URL - PASTIKAN INI ADALAH URL GOOGLE SCRIPT
const GOOGLE_SCRIPT_URL = 'https://script.google.com/macros/s/AKfycbyLEjp-p9CFvpu9lLEfz1I7TPA1iLeYO4KEJVISKnl5AfReHVM3yqzoteNaNjMOJoAS0g/exec';

// Kategori HPP yang baru
const CATEGORIES = {
  'Makanan': [
    'Kids Menu',
    'Appetizer',
    'Main Course',
    'Dessert',
    'Breakfast Menu',
    'Veggies',
    'Others'
  ],
  'Minuman': [
    'Signature',
    'Espresso Based',
    'Single Origin',
    'Frappuccino',
    'Milkshake',
    'Ice Cream',
    'Non Coffee'
  ]
};

const App = () => {
  // States
  const [brand, setBrand] = useState('');
  const [targetCost, setTargetCost] = useState('');
  const [targetPieces, setTargetPieces] = useState('');
  const [recipeName, setRecipeName] = useState('');
  const [recipeCategory, setRecipeCategory] = useState('Main Course');
  const [recipeSubCategory, setRecipeSubCategory] = useState('');
  const [saveStatus, setSaveStatus] = useState({ type: '', message: '' });
  const [isLoading, setIsLoading] = useState(false);
  const [connectionStatus, setConnectionStatus] = useState('checking');
  const [lastCheck, setLastCheck] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editRecipeId, setEditRecipeId] = useState(null);

  // Ingredients state
  const [ingredients, setIngredients] = useState([
    { id: 1, name: '', usage: '', unit: 'gr', purchasePrice: '', purchaseUnit: '' }
  ]);

  // Consumable state
  const [consumable, setConsumable] = useState({
    name: 'Packaging',
    cost: '',
    quantity: '1',
    unit: 'unit'
  });

  // Percentages
  const [goFoodPercentage, setGoFoodPercentage] = useState(20);
  const [taxPercentage, setTaxPercentage] = useState(10);
  const [profitMargin, setProfitMargin] = useState(40);

  // History
  const [recipeHistory, setRecipeHistory] = useState([]);
  const [showHistory, setShowHistory] = useState(false);
  const [availableRecipes, setAvailableRecipes] = useState([]);
  const [showRecipeSelector, setShowRecipeSelector] = useState(false);

  // Update subcategories ketika kategori utama berubah
  useEffect(() => {
    // Set subcategory pertama sebagai default ketika kategori berubah
    const subCategories = CATEGORIES[recipeCategory] || [];
    if (subCategories.length > 0 && !recipeSubCategory) {
      setRecipeSubCategory(subCategories[0]);
    }
  }, [recipeCategory, recipeSubCategory]);

  // ===== UTILITY FUNCTIONS =====
  const formatRupiah = (number) => {
    if (isNaN(number) || number === 0) return 'Rp 0';
    return `Rp ${Math.round(number).toLocaleString('id-ID')}`;
  };

  // ===== CALCULATION FUNCTIONS =====
  const calculateIngredientCost = (ing) => {
    const usage = parseFloat(ing.usage) || 0;
    const purchaseUnit = parseFloat(ing.purchaseUnit) || 1;
    const purchasePrice = parseFloat(ing.purchasePrice) || 0;
    return purchaseUnit > 0 ? (usage / purchaseUnit) * purchasePrice : 0;
  };

  const calculateTotalMaterialCost = () => {
    return ingredients.reduce((total, ing) => total + calculateIngredientCost(ing), 0);
  };

  const calculateTotalProductionCost = () => {
    return calculateTotalMaterialCost() + (parseFloat(consumable.cost) || 0);
  };

  const calculateHPPPerPiece = () => {
    const total = calculateTotalProductionCost();
    const pieces = parseFloat(targetPieces) || 1;
    return pieces > 0 ? total / pieces : 0;
  };

  const calculateDineInPrice = () => {
    const hpp = calculateHPPPerPiece();
    const margin = profitMargin / 100;
    if (margin >= 1) return hpp * 2;
    return margin > 0 ? hpp / (1 - margin) : hpp;
  };

  const calculateGoFoodCost = () => {
    return calculateDineInPrice() * (goFoodPercentage / 100);
  };

  const calculateRestaurantTax = () => {
    return calculateDineInPrice() * (taxPercentage / 100);
  };

  const calculateGoFoodPrice = () => {
    return calculateDineInPrice() + calculateGoFoodCost() + calculateRestaurantTax();
  };

  const calculateGrossProfit = () => {
    return calculateDineInPrice() - calculateHPPPerPiece();
  };

  // ===== MANAJEMEN BAHAN =====
  const updateIngredient = (id, field, value) => {
    setIngredients(ingredients.map(ingredient =>
      ingredient.id === id ? { ...ingredient, [field]: value } : ingredient
    ));
  };

  const addIngredient = () => {
    const newId = ingredients.length > 0 ? Math.max(...ingredients.map(i => i.id)) + 1 : 1;
    setIngredients([
      ...ingredients,
      { id: newId, name: '', usage: '', unit: 'gr', purchasePrice: '', purchaseUnit: '' }
    ]);
  };

  const removeIngredient = (id) => {
    if (ingredients.length > 1) {
      setIngredients(ingredients.filter(ingredient => ingredient.id !== id));
    }
  };

  // ===== RESET =====
  const resetAllData = () => {
    setBrand('');
    setTargetCost('');
    setTargetPieces('');
    setRecipeName('');
    setRecipeCategory('Makanan');
    setRecipeSubCategory('Main Course');
    setIngredients([{ id: 1, name: '', usage: '', unit: 'gr', purchasePrice: '', purchaseUnit: '' }]);
    setConsumable({ name: 'Packaging', cost: '', quantity: '1', unit: 'unit' });
    setGoFoodPercentage(20);
    setTaxPercentage(10);
    setProfitMargin(40);
    setSaveStatus({ type: '', message: '' });
    setEditMode(false);
    setEditRecipeId(null);
  };

  // ===== TEST CONNECTION - IMPROVED VERSION =====
  const testConnection = async () => {
    try {
      setConnectionStatus('checking');
      setSaveStatus({ type: 'loading', message: 'ðŸ”„ Testing connection to Google Sheets...' });

      console.log('Testing connection to Google Script:', GOOGLE_SCRIPT_URL);

      // Approach 1: Coba GET request dulu (biasanya lebih reliable)
      console.log('Trying GET request...');
      let response = await fetch(GOOGLE_SCRIPT_URL, {
        method: 'GET',
        mode: 'no-cors', // Coba dengan no-cors untuk menghindari CORS error
      }).catch(e => {
        console.log('GET with no-cors failed:', e.message);
        return null;
      });

      // Approach 2: Coba POST request dengan test_connection
      if (!response) {
        console.log('Trying POST request...');
        const testData = {
          action: 'ping',
          timestamp: new Date().toISOString(),
          source: 'Netlify App'
        };

        response = await fetch(GOOGLE_SCRIPT_URL, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(testData)
        });
      }

      // Jika response ada dan ok
      if (response && response.ok) {
        try {
          const result = await response.json();
          console.log('âœ… Connection successful:', result);

          setConnectionStatus('connected');
          setLastCheck(new Date());

          setSaveStatus({ 
            type: 'success', 
            message: `âœ… Connected to Google Sheets API! Status: ${result.status || 'online'}` 
          });

        } catch (jsonError) {
          console.log('JSON parse error, but request went through');
          setConnectionStatus('connected');
          setLastCheck(new Date());
          setSaveStatus({ 
            type: 'success', 
            message: 'âœ… Connected to Google Script (response ok)' 
          });
        }
      } else {
        // Request berhasil dikirim tapi tidak dapat response JSON
        setConnectionStatus('connected');
        setLastCheck(new Date());
        setSaveStatus({ 
          type: 'warning', 
          message: 'âš ï¸ Connected but may have CORS restrictions' 
        });
      }

    } catch (error) {
      console.error('âŒ Connection test error:', error);
      setConnectionStatus('error');
      setLastCheck(new Date());

      let errorMessage = 'Connection failed: ';

      if (error.message.includes('Failed to fetch')) {
        errorMessage += 'Network error. Check: 1. Internet connection 2. Google Script URL 3. CORS settings';
      } else if (error.message.includes('CORS')) {
        errorMessage += 'CORS policy blocked the request. Try from same origin.';
      } else {
        errorMessage += error.message;
      }

      setSaveStatus({ 
        type: 'error', 
        message: `âŒ ${errorMessage}` 
      });

      // Fallback: Test dengan method sederhana
      try {
        console.log('Trying simple ping...');
        const testUrl = `${GOOGLE_SCRIPT_URL}?test=${Date.now()}`;
        const img = new Image();
        img.src = testUrl;

        setTimeout(() => {
          console.log('Ping attempt completed');
        }, 1000);
      } catch (pingError) {
        console.log('Ping also failed');
      }
    }
  };

  // ===== LOAD RECIPES FROM GOOGLE SHEETS =====
  const loadRecipesFromGoogleSheets = async () => {
    try {
      setIsLoading(true);
      setSaveStatus({ type: 'loading', message: 'ðŸ“¥ Loading recipes from Google Sheets...' });

      const response = await fetch(GOOGLE_SCRIPT_URL, {
        method: 'POST',
        mode: 'no-cors',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'get_recipes'
        })
      }).catch(async (fetchError) => {
        console.log('Fetch failed, trying alternative method');
        return { ok: false };
      });

      console.log('âœ… Request to get recipes sent');

      // Karena menggunakan no-cors, kita tidak bisa membaca response langsung
      // Simulasi data untuk demo (dalam implementasi nyata, Google Script perlu diperbarui)
      setTimeout(() => {
        const mockRecipes = [
          { id: 1, name: 'Spaghetti Carbonara', category: 'Makanan', subcategory: 'Main Course', brand: 'Italian Delight', date: '2024-01-25' },
          { id: 2, name: 'Chocolate Cake', category: 'Makanan', subcategory: 'Dessert', brand: 'Sweet Treats', date: '2024-01-24' },
          { id: 3, name: 'Cappuccino', category: 'Minuman', subcategory: 'Espresso Based', brand: 'Coffee House', date: '2024-01-23' },
        ];

        setAvailableRecipes(mockRecipes);
        setShowRecipeSelector(true);
        setIsLoading(false);
        setSaveStatus({ type: 'info', message: 'âœ… Recipes loaded. Select one to edit.' });
      }, 1500);

    } catch (error) {
      console.error('âŒ Error loading recipes:', error);
      setIsLoading(false);
      setSaveStatus({ type: 'error', message: 'Failed to load recipes. Please try again.' });
    }
  };

  // ===== LOAD SPECIFIC RECIPE FOR EDITING =====
  const loadRecipeForEditing = async (recipeId) => {
    try {
      setIsLoading(true);
      setSaveStatus({ type: 'loading', message: 'ðŸ“¥ Loading recipe data...' });

      // Dalam implementasi nyata, ini akan memanggil Google Script untuk mendapatkan data spesifik
      // Untuk demo, kita gunakan data mock
      setTimeout(() => {
        // Data contoh untuk editing
        const mockRecipeData = {
          recipe_name: 'Spaghetti Carbonara',
          recipe_category: 'Makanan',
          recipe_subcategory: 'Main Course',
          brand: 'Italian Delight',
          target_cost: 35000,
          target_pieces: 10,
          profit_margin: 40,
          gofood_percentage: 20,
          tax_percentage: 10,
          ingredients: [
            { name: 'Spaghetti', usage: '500', unit: 'gr', purchasePrice: '25000', purchaseUnit: '1000' },
            { name: 'Eggs', usage: '4', unit: 'pcs', purchasePrice: '30000', purchaseUnit: '30' },
            { name: 'Parmesan Cheese', usage: '200', unit: 'gr', purchasePrice: '75000', purchaseUnit: '1000' }
          ],
          packaging: {
            name: 'Packaging',
            cost: '5000',
            quantity: '1',
            unit: 'unit'
          }
        };

        // Isi form dengan data recipe
        setRecipeName(mockRecipeData.recipe_name);
        setRecipeCategory(mockRecipeData.recipe_category);
        setRecipeSubCategory(mockRecipeData.recipe_subcategory);
        setBrand(mockRecipeData.brand);
        setTargetCost(mockRecipeData.target_cost.toString());
        setTargetPieces(mockRecipeData.target_pieces.toString());
        setProfitMargin(mockRecipeData.profit_margin);
        setGoFoodPercentage(mockRecipeData.gofood_percentage);
        setTaxPercentage(mockRecipeData.tax_percentage);

        // Isi ingredients
        const formattedIngredients = mockRecipeData.ingredients.map((ing, index) => ({
          id: index + 1,
          name: ing.name,
          usage: ing.usage,
          unit: ing.unit,
          purchasePrice: ing.purchasePrice,
          purchaseUnit: ing.purchaseUnit
        }));
        setIngredients(formattedIngredients);

        // Isi packaging
        setConsumable({
          name: mockRecipeData.packaging.name,
          cost: mockRecipeData.packaging.cost,
          quantity: mockRecipeData.packaging.quantity,
          unit: mockRecipeData.packaging.unit
        });

        setEditMode(true);
        setEditRecipeId(recipeId);
        setShowRecipeSelector(false);
        setIsLoading(false);
        setSaveStatus({ type: 'success', message: 'âœ… Recipe loaded for editing. Make changes and click "Update Recipe".' });
      }, 1000);

    } catch (error) {
      console.error('âŒ Error loading recipe for editing:', error);
      setIsLoading(false);
      setSaveStatus({ type: 'error', message: 'Failed to load recipe. Please try again.' });
    }
  };

  // ===== LOCAL CACHE =====
  const saveToLocalCache = (summaryData, ingredientsData) => {
    try {
      const cacheData = {
        ...summaryData,
        ingredients: ingredientsData,
        cached_at: new Date().toISOString()
      };

      const existingCache = JSON.parse(localStorage.getItem('hpp_cache') || '[]');
      existingCache.unshift(cacheData);
      localStorage.setItem('hpp_cache', JSON.stringify(existingCache.slice(0, 20)));
      setRecipeHistory(existingCache.slice(0, 20));
    } catch (error) {
      console.error('Cache error:', error);
    }
  };

  const loadFromCache = (recipe) => {
    setRecipeName(recipe.recipe_name);
    setRecipeCategory(recipe.recipe_category || 'Makanan');
    setRecipeSubCategory(recipe.recipe_subcategory || 'Main Course');
    setBrand(recipe.brand);
    setTargetCost(recipe.target_cost?.toString() || '');
    setTargetPieces(recipe.target_pieces?.toString() || '');
    setProfitMargin(recipe.profit_margin || 40);
    setGoFoodPercentage(recipe.gofood_percentage || 20);
    setTaxPercentage(recipe.tax_percentage || 10);

    setSaveStatus({ type: 'info', message: 'ðŸ“‚ Recipe loaded. Re-enter ingredients.' });
    setShowHistory(false);
  };

  // ===== SIMPAN KE GOOGLE SHEETS =====
  const saveToGoogleSheets = async (isUpdate = false) => {
    // Validasi
    if (!recipeName.trim()) {
      setSaveStatus({ type: 'error', message: 'âš ï¸ Please enter recipe name' });
      return;
    }

    const invalidIngredients = ingredients.filter(ing => 
      !ing.name.trim() || !ing.usage || !ing.purchasePrice || !ing.purchaseUnit
    );

    if (invalidIngredients.length > 0) {
      setSaveStatus({ 
        type: 'error', 
        message: `âš ï¸ Please complete ${invalidIngredients.length} ingredient(s)` 
      });
      return;
    }

    setIsLoading(true);
    setSaveStatus({ type: 'loading', message: isUpdate ? 'ðŸ”„ Updating recipe...' : 'ðŸ“¤ Sending to Google Sheets...' });

    let summaryData = null;
    let ingredientsData = null;

    try {
      // Prepare data
      const now = new Date();
      const timestamp = now.toLocaleString('id-ID');

      summaryData = {
        timestamp: timestamp,
        recipe_name: recipeName.trim(),
        recipe_category: recipeCategory,
        recipe_subcategory: recipeSubCategory,
        brand: brand.trim() || '-',
        target_cost: parseFloat(targetCost) || 0,
        target_pieces: parseFloat(targetPieces) || 0,
        total_material_cost: calculateTotalMaterialCost(),
        packaging_cost: parseFloat(consumable.cost) || 0,
        total_production_cost: calculateTotalProductionCost(),
        hpp_per_piece: calculateHPPPerPiece(),
        profit_margin: profitMargin,
        dine_in_price: calculateDineInPrice(),
        gofood_percentage: goFoodPercentage,
        tax_percentage: taxPercentage,
        gofood_price: calculateGoFoodPrice(),
        gross_profit: calculateGrossProfit(),
        status: isUpdate ? 'UPDATED' : 'ACTIVE'
      };

      ingredientsData = ingredients.map((ing, index) => ({
        recipe_name: recipeName.trim(),
        ingredient_no: index + 1,
        ingredient_name: ing.name.trim(),
        usage_amount: parseFloat(ing.usage) || 0,
        usage_unit: ing.unit,
        purchase_price: parseFloat(ing.purchasePrice) || 0,
        purchase_unit: parseFloat(ing.purchaseUnit) || 1,
        ingredient_cost: calculateIngredientCost(ing)
      }));

      const packagingData = {
        recipe_name: recipeName.trim(),
        item_name: consumable.name,
        cost: parseFloat(consumable.cost) || 0,
        quantity: consumable.quantity,
        unit: consumable.unit
      };

      const allData = {
        action: isUpdate ? 'update_recipe' : 'save_recipe',
        timestamp: timestamp,
        recipe_id: editRecipeId, // Untuk update
        summary: summaryData,
        ingredients: ingredientsData,
        packaging: packagingData,
        source: 'Netlify App'
      };

      console.log(`${isUpdate ? 'ðŸ”„ Updating' : 'ðŸ“¨ Sending'} data to Google Script:`, allData);

      // Send to Google Sheets dengan error handling khusus untuk CORS
      const response = await fetch(GOOGLE_SCRIPT_URL, {
        method: 'POST',
        mode: 'no-cors', // Gunakan no-cors untuk menghindari CORS error
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(allData)
      }).catch(async (fetchError) => {
        console.log('Fetch failed, trying alternative method:', fetchError);

        // Fallback: Gunakan form submission method
        return await tryFormSubmission(allData);
      });

      // Dengan mode 'no-cors', kita tidak bisa membaca response
      // Tapi request sudah terkirim ke Google Script
      console.log(`âœ… Request ${isUpdate ? 'update' : 'save'} sent to Google Script (no-cors mode)`);

      // Simpan ke cache lokal sebagai backup
      if (summaryData && ingredientsData) {
        saveToLocalCache(summaryData, ingredientsData);
      }

      // Beri feedback ke user
      setTimeout(() => {
        setSaveStatus({ 
          type: 'success', 
          message: `âœ… Recipe "${recipeName}" ${isUpdate ? 'updated' : 'saved'} to Google Sheets! Data may take a few seconds to appear.` 
        });

        // Reset form setelah 3 detik
        setTimeout(() => {
          resetAllData();
          setSaveStatus({ 
            type: 'info', 
            message: 'ðŸ“ Form cleared. Ready for next recipe!' 
          });
        }, 3000);
      }, 1000);

    } catch (error) {
      console.error(`âŒ ${isUpdate ? 'Update' : 'Save'} error:`, error);

      // Simpan ke cache lokal sebagai fallback
      if (summaryData && ingredientsData) {
        saveToLocalCache(summaryData, ingredientsData);
      }

      setSaveStatus({ 
        type: 'warning', 
        message: `âš ï¸ ${isUpdate ? 'Update' : 'Save'} may be delayed. Error: ${error.message}` 
      });

    } finally {
      setIsLoading(false);
    }
  };

  // Alternative method untuk mengirim data jika fetch gagal
  const tryFormSubmission = (data) => {
    return new Promise((resolve) => {
      try {
        // Buat form tersembunyi
        const form = document.createElement('form');
        const iframe = document.createElement('iframe');

        iframe.name = 'google-script-iframe';
        iframe.style.display = 'none';
        document.body.appendChild(iframe);

        form.target = 'google-script-iframe';
        form.action = GOOGLE_SCRIPT_URL;
        form.method = 'POST';
        form.style.display = 'none';

        // Tambahkan data sebagai input
        const input = document.createElement('input');
        input.name = 'data';
        input.value = JSON.stringify(data);
        form.appendChild(input);

        document.body.appendChild(form);
        form.submit();

        // Cleanup setelah 3 detik
        setTimeout(() => {
          document.body.removeChild(form);
          document.body.removeChild(iframe);
          resolve({ ok: true });
        }, 3000);

      } catch (error) {
        console.log('Form submission also failed:', error);
        resolve({ ok: false });
      }
    });
  };

  // ===== AUTO CHECK CONNECTION =====
  useEffect(() => {
    // Auto-check connection on load
    testConnection();

    // Load cache
    const cached = JSON.parse(localStorage.getItem('hpp_cache') || '[]');
    setRecipeHistory(cached);

    // Periodic connection check (every 2 minutes)
    const interval = setInterval(() => {
      if (connectionStatus !== 'connected') {
        testConnection();
      }
    }, 120000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="container mt-3">
      {/* Header */}
      <div className="header-section text-center mb-4">
        <h1 className="text-primary">ðŸš€ HPP Calculator - Production</h1>
        <p className="text-muted">Connected to Google Sheets</p>

        {/* Connection Status */}
        <div className="d-flex justify-content-center align-items-center mb-3">
          <div className={`badge ${connectionStatus === 'connected' ? 'bg-success' : 
                           connectionStatus === 'error' ? 'bg-danger' : 'bg-warning'} me-2`}
               style={{ fontSize: '0.9rem', padding: '6px 12px' }}>
            {connectionStatus === 'connected' ? 'âœ… ONLINE' : 
             connectionStatus === 'error' ? 'âŒ OFFLINE' : 'âŒ› CHECKING...'}
          </div>

          <button 
            className="btn btn-sm btn-outline-secondary ms-2"
            onClick={testConnection}
            disabled={isLoading}
          >
            {isLoading ? (
              <span className="spinner-border spinner-border-sm me-1"></span>
            ) : (
              'ðŸ”„ Test'
            )}
          </button>

          {lastCheck && (
            <small className="text-muted ms-2">
              Last check: {lastCheck.toLocaleTimeString()}
            </small>
          )}
        </div>

        {/* Status Message */}
        {saveStatus.message && (
          <div className={`alert ${saveStatus.type === 'success' ? 'alert-success' : 
                           saveStatus.type === 'error' ? 'alert-danger' : 
                           saveStatus.type === 'loading' ? 'alert-info' : 
                           'alert-warning'} alert-dismissible fade show`} 
               style={{maxWidth: '800px', margin: '0 auto'}}>
